sition of each SNP arrange(CHR, BP) %&gt;% mutate( BPcum=BP+tot) %&gt;% # Add highlight and annotation information mutate( is_highlight=ifelse(SNP %in% snpsOfInterest, &quot;yes&quot;, &quot;no&quot;)) %&gt;% # Filter SNP to make the plot lighter filter(-log10(P)&gt;0.5) # Prepare X axis axisdf &lt;- don %&gt;% group_by(CHR) %&gt;% summarize(center=( max(BPcum) + min(BPcum) ) / 2 ) # Prepare text description for each SNP: don$text &lt;- paste(&quot;SNP: &quot;, don$SNP, &quot;
